import React, {Component} from 'react'
import axios from 'axios'

class EditWorkout extends Component {
constructor(props) {
    super(props)

    this.state = {
        name: "",
        description: "",
        difficulty: "",
        duration: 0
      };

      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }

    async handleChange(event) {
      const name = event.target.name;
      const value = event.target.value;
      this.setState({
        [name]: value
      })
      }

      async handleSubmit(event) {
      event.preventDefault();
      await axios.post('http://localhost:3001/workouts', {
        name: this.state.name,
        description: this.state.description,
        difficulty: this.state.difficulty,
        duration: this.state.duration
      })
      this.setState({
        redirect: true
      })
      }





handleChange = (event) => {
const name = event.target.name;
const value = event.target.value;
this.setState({
  [name]: value
})
}

handleSubmit = async (event) => {
event.preventDefault();
await axios.put(`/icecreams/${this.props.match.params.id}`, {
  flavor: this.state.flavor,
  description: this.state.description,
  rating: this.state.rating,
})
this.setState({
  redirect: true
})
}


    render() {
        return (
            <div>
          {this.state.redirect ? <Redirect to="/home"/> : null}
        <form onChange={this.handleChange} onSubmit={this.handleSubmit}>
          <input
            name="name"
            type="text"
            placeholder="Workout Name"
            value={this.state.name}
          />
          <input
            name="description"
            type="textarea"
            placeholder="Description"
            value={this.state.description}
          />
          <select onClick={this.handleChange} onSubmit={this.handleSubmit} name="difficulty" type="text" value={this.state.difficulty}>
            <option>Easy</option>
            <option>Moderate</option>
            <option>Advanced</option>
          </select>
          <input name="duration" type="integer" value={this.state.duration} />
          <br />
          <button type="submit">Submit</button>
        </form>
      </div>
        )
    }
}

export default EditWorkout
