import React, { Component } from 'react'

class OneWorkout extends Component {
    constructor(props){
        super(props)

    this.state = {
        description: "",
        flavor: "",
        rating: 0,
        redirect: false
      };
    }

    componentDidMount = async () => {
      const response = await axios.get(
        `/icecreams/${this.props.match.params.id}`
      );
      const iceCreamObj = response.data.iceCream;
      this.setState({
        description: iceCreamObj.description,
        flavor: iceCreamObj.flavor,
        rating: iceCreamObj.rating,
        review: iceCreamObj.reviews
      });
    };

    handleDelete = async () => {
      await axios.delete(`/icecreams/${this.props.match.params.id}`);
      this.setState({
        redirect: true
      })
    };
    render() {
        return (
            <div>One workout

            </div>
        )
    }
}

export default OneWorkout;
