import React, {Component} from 'react'
import axios from 'axios'
import {Redirect} from 'react-router-dom'

class EditWorkout extends Component {
constructor(props) {
    super(props)

    this.state = {
        name: "",
        description: "",
        difficulty: "",
        duration: 0
      };

      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }

    componentDidMount = async () => {
        const response = await axios.get(
          `http://localhost:3001/workouts/${this.props.match.params.id}`
        );
        const workout = response.data.oneWorkOut
        this.setState({
          description: workout.description,
          name: workout.name,
          difficulty: workout.difficulty,
          duration: workout.duration
        });
      };

    async handleChange(event) {
      const name = event.target.name;
      const value = event.target.value;
      this.setState({
        [name]: value
      })
      }

      async handleSubmit(event) {
      event.preventDefault();
      await axios.put('http://localhost:3001/workouts', {
        name: this.state.name,
        description: this.state.description,
        difficulty: this.state.difficulty,
        duration: this.state.duration
      })
      this.setState({
        redirect: true
      })
      }

    render() {
        return (
            <div>
          {this.state.redirect ? <Redirect to="/home"/> : null}
        <form onChange={this.handleChange} onSubmit={this.handleSubmit}>
          <input
            name="name"
            type="text"
            placeholder="Workout Name"
            value={this.state.name}
          />
          <input
            name="description"
            type="textarea"
            placeholder="Description"
            value={this.state.description}
          />
          <select onChange={this.handleOptionChange} name="difficulty" type="text" value={this.state.difficulty}>
            <option>Easy</option>
            <option>Moderate</option>
            <option>Advanced</option>
          </select>
          <input name="duration" type="integer" value={this.state.duration} />
          <br />
          <button type="submit">Submit</button>
        </form>
      </div>
        )
    }
}

export default EditWorkout
