import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default class PersonalWorkoutRegimen extends Component {
  constructor(props) {
    super(props);
    // this.state = {
    //   workoutsArray: []
    // };
  }

   componentDidMount() {
    this.props.getRegimen();
  }

  // getRegimen = async () => {
  //   const result = await axios.get(
  //     `http://localhost:3001/workouts/regimen/true`
  //   );
  //   const workoutsArray = result.data.result;
  //   this.setState({
  //     workoutsArray
  //   });
  // }

  handleClicks = async (id) => {
    await axios.put(`http://localhost:3001/workouts/regimen/${id}`, {
      regimen: false
    });
    this.props.getWorkouts()
    this.props.getRegimen()
    // this.state.workoutsArray.splice(i, 1)
  }

  render() {
    return (
      <div>
        {this.props.regimenArray.map((workout, i) => (
          <div>
            <h3>{workout.name}</h3>
            <p>Description: {workout.description}</p>
            <p>Duration: {workout.duration}</p>
            <p>Difficulty: {workout.difficulty}</p>
            <button onClick={() => {this.handleClicks(workout.id)}}>
              Delete from PWR
            </button>
          </div>
        ))}
      </div>
    )
  }
}
